	//预编译正则表达式，解析更快速，调试也方便
	Pattern pt_uid = Pattern.compile("uid=(\\d+)");
	Pattern pt_ouid = Pattern.compile("weibo.cn/u/(\\d+)");
	Pattern pt_ouid_2 = Pattern.compile("weibo.cn/(\\d+)");
	Pattern pt_ouid_3 = Pattern.compile("weibo.cn/(\\w+)");
	Pattern pt_owid = Pattern.compile("comment/(\\w+)?");
	Pattern pt_count = Pattern.compile("(\\d+)");
	private ArrayList<UserWeiboPojo> parsePage(String page){
		if(StringUtil.isBlank(page))
			return null;
		ArrayList<UserWeiboPojo> uwp = new ArrayList<UserWeiboPojo>();	
		boolean isOriginal =true;//初始化isOriginal
		Element body = JsoupUtil.getBody(page);
		//0.解析粉丝数、微博数、关注数
		Elements tip = body.select("div.u").first().select("div.tip2");
		String weiboNum = tip.select("span.tc").text();
		String focusNum = tip.select("a[href*=/follow]").text();
		String fansNum = tip.select("a[href*=/fans]").text();
		String weiboCount = parseUtil.matchStringGroup1(weiboNum,pt_count);
		String focusCount = parseUtil.matchStringGroup1(focusNum,pt_count);
		String fansCount = parseUtil.matchStringGroup1(fansNum,pt_count);

		//1.用戶id:uid
		Element user = body.select("div.u").first();
		Elements userInfo =user.select("div.tip2");
		String userId = pu.matchStringGroup1(userInfo.toString(), pt_uid);
		//System.out.println("userID="+userId);
		if(StringUtils.isBlank(userId))//如果UID为空，就不要这些微博数据了
			return null;
		//2.拿微博
		//拿到所有微博节点
		Elements weibos = body.select("div.c");
		for(Element weibo:weibos){
			UserWeiboPojo wpj = new UserWeiboPojo();
			//2.1微博id
			String wid = weibo.attr("id");
			if(StringUtils.isBlank(wid))//不一定所有class=c的div都是包含微博信息的，没有的就不是微博节点
				continue;
			int n=9;	//处理“M_ ”
			wid=wid.substring(wid.length()-n,wid.length()); 
			String eClassCText = weibo.text();
			if(eClassCText.contains("该微博已被删除")) //异常微博
				continue;
			System.out.println("wid="+wid);
			//2.2微博内容
			Element childNode;
			childNode = weibo.select("span.ctt").first();
			String wbContent ="";
			if( childNode != null){
				wbContent = childNode.text().trim();
			}
			//2.3微博图片链接抓取
			String mediaSrc = "";
			mediaSrc =weibo.select("div").select("a").select("img.ib").attr("src");
			//2.4处理转发微博及其相关信息
			Element cmt = weibo.select("span.cmt").first();
			String ouid = "";
			String OriginalSrc = "";
			String rcmt = "";
			String owid = "";
			if(cmt != null){	//确定为转发微博
				String repost = cmt.text().trim();
				isOriginal =false;
				wbContent = repost+wbContent;
				ouid = pu.matchStringGroup1(cmt.select("a").attr("href"),pt_ouid);
				if(ouid == null) 
					ouid = pu.matchStringGroup1(cmt.select("a").attr("href"),pt_ouid_2);
				//如果还是没有ouid,则证明uid无法解析到，只能解析到个性化Uid
				if(ouid == null)
					ouid = pu.matchStringGroup1(cmt.select("a").attr("href"),pt_ouid_3);
				OriginalSrc = weibo.select("a.cc").first().attr("href");;
				rcmt = pu.matchStringGroup1(eClassCText, "转发理由:(.+?)赞");
				owid = pu.matchStringGroup1(OriginalSrc, pt_owid);
			}
			
			//2.5设置时间和来源
			childNode = weibo.select("span.ct").first();//时间戳
			if( childNode!=null ){
				String timeStr = childNode.text();
				String wbTime = "";
				if(pu.isMatch(timeStr, "分钟前")){//x分钟前格式时间戳
					Calendar calendar = Calendar.getInstance();
					calendar.setTime(new Date());
					calendar.add(Calendar.MINUTE, +1);//+1今天的时间加一天		
					Date date = calendar.getTime();		
					wbTime = new SimpleDateFormat("yyyy-MM-dd HH:mm").format(date);
					//System.out.println("分钟前"+timeStr);
				}
				else if(pu.isMatch(timeStr, "刚刚")){//“刚刚” 格式时间戳
					wbTime = new SimpleDateFormat("yyyy-MM-dd HH:mm").format(new Date());
					//System.out.println("刚刚"+timeStr);
				}
				else //XXXX年XX月XX日 或者 XX月XX日 或者 今天 XX:XX 
				{
					if(pu.isMatch(timeStr, "今天")){
						wbTime = new SimpleDateFormat("yyyy-MM-dd ").format(new Date());
						//System.out.println("今天"+timeStr);
					}
					else if(pu.isMatch(timeStr, "\\d{4}-\\d{2}-\\d{2}")){
						wbTime += pu.matchStringGroup1(timeStr, "(\\d+)-")+"-";
						wbTime += pu.matchStringGroup1(timeStr, "-(\\d\\d)-")+"-";
						wbTime += pu.matchStringGroup1(timeStr, "-(\\d\\d) ")+" ";
						//System.out.println("年份"+timeStr);
					}
					else if(pu.isMatch(timeStr, "\\d{2}月\\d{2}日")){
						wbTime += new SimpleDateFormat("yyyy-").format(new Date());
						wbTime += pu.matchStringGroup1(timeStr, "(\\d\\d)月")+"-";
						wbTime += pu.matchStringGroup1(timeStr, "(\\d\\d)日")+" ";
						//System.out.println("几月几日"+timeStr);
					}
					else
						wbTime = "";
					//获取时间
					wbTime += pu.matchStringGroup1(timeStr, "(\\d\\d:\\d\\d)");
				}
				String wbSource = null;
				if(timeStr !=null)
					wbSource=pu.matchStringGroup1(timeStr, "来自(.+)");
				System.out.println("wbSource="+wbSource);
				wpj.setTime(wbTime);
				wpj.setSource(wbSource);
			}//time
			//2.6收集 赞、转发、评论数(区别与原文转发、赞和评论)
			String likeCountStr = "";
			String repostCountStr ="";
			String commentCountStr ="";
			Elements forCount =weibo.select("a");
			likeCountStr = pu.matchStringGroup1(forCount.text(), "赞\\[(\\d+)\\]") ;
			repostCountStr = pu.matchStringGroup1(forCount.text(), "转发\\[(\\d+)\\]");
			commentCountStr = pu.matchStringGroup1(forCount.text(), "评论\\[(\\d+)\\]");
			//正常没匹配到就进行全文匹配
			if(likeCountStr ==null ||likeCountStr =="") pu.matchStringGroup1(eClassCText, "赞\\[(\\d+)\\]") ;
			if(repostCountStr ==null || repostCountStr =="") pu.matchStringGroup1(eClassCText, "转发\\[(\\d+)\\]");
			if(commentCountStr ==null || commentCountStr =="") pu.matchStringGroup1(eClassCText, "评论\\[(\\d+)\\]");
			//全文都没匹配到就直接置为0
			if(likeCountStr ==null ||likeCountStr =="") likeCountStr="0";
			if(repostCountStr ==null || repostCountStr =="") repostCountStr="0";
			if(commentCountStr ==null || commentCountStr =="") commentCountStr="0";
			wpj.setUid(userId);
			wpj.setWid(wid);
			wpj.setContent(wbContent);
			wpj.setMediaSrc(mediaSrc);
			wpj.setIsOriginal(isOriginal);
			wpj.setOuid(ouid);
			wpj.setOwid(owid);
			wpj.setRcmt(rcmt);
			wpj.setLikeCount(likeCountStr);
			wpj.setCommentCount(commentCountStr);
			wpj.setRepostCount(repostCountStr);
			wpj.setWeibocount(weiboCount);
			wpj.setFollowercount(fansCount);
			wpj.setFriendcount(focusCount);	
			System.out.println("解析到微博信息："+wpj);
			isOriginal = true;//归位
			uwp.add(wpj);
		}//for
		return uwp;
	}
	private void storePojo(ArrayList<UserWeiboPojo> uwp) throws IOException{
		if(uwp!=null && uwp.size()!=0){
			FileUtil fu = new FileUtil();
			fu.writeFile(uwp.get(0).toCountJson(),"./weiboUser/"+uwp.get(0).getUid()+"/count.json" );
			for(int i=0;i<uwp.size();i++){
				if(uwp.get(i).getUid() !=null && uwp.get(i).getUid().hashCode() != "".hashCode()){
					fu.writeFileAppend(uwp.get(i).toJson(),"./weiboUser/"+uwp.get(i).getUid()+"/weibo/weibo.json");
				}else{
					System.err.println("用户ID为空，舍弃当前微博信息！");
				}
			}
			System.out.println("存储一页微博成功！");
		}else{
			System.err.println("存储一页微博0个！");
		}
	}
	
	public static void main(String[] args) {
		parseUserWeiboPage pu =new parseUserWeiboPage();
		pu.parsePageAndStore();
	}
